mapper.select:
select o.id as orderId, o.real_pay_amount as realPayAmount, o.post_amount as postAmount, o.pay_method as payMethod, o.created_time as createdTime, o.is_comment as isComment, os.order_status as orderStatus from orders o left join order_status os on o.id = os.order_id where o.user_id = #{paramsMap.userId} and o.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
order by o.updated_time asc

select o.id as orderId, o.real_pay_amount as realPayAmount, o.post_amount as postAmount, o.pay_method as payMethod, o.created_time as createTime, oi.item_id as itemId, oi.item_name as itemName, oi.item_img as itemImg, oi.item_spec_id as itemSpecId, oi.item_spec_name as itemSpecName, oi.price as price, oi.buy_counts as buyCounts, os.order_status as orderStatus from orders o left join order_items oi on o.id = oi.order_id left join order_status os on o.id = os.order_id where o.user_id = #{paramsMap.userId} and o.is_delete = 0
and os.order_status = #{paramsMap.orderStatus}
order by o.updated_time asc

select count(1)
from orders o
left join order_status os on o.id = os.order_id
where o.user_id = #{paramsMap.userId}
and os.order_status = #{paramsMap.orderStatus}

    and o.is_comment = #{paramsMap.isComment}
